import React, { useState, useEffect } from 'react';
import '../styles/ChatPage.css';
import ChatListItem from '../components/ChatList';

function ChatPage({ socket }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [currentRoom, setCurrentRoom] = useState('global');

  useEffect(() => {
    if (!socket) return;

    socket.emit('joinRoom', { roomId: currentRoom });

    socket.on('receiveMessage', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off('receiveMessage');
    };
  }, [socket, currentRoom]);

  const sendMessage = () => {
    if (message.trim() && socket) {
      socket.emit('sendMessage', { roomId: currentRoom, message });
      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-sidebar">
        <h3>Chat Rooms</h3>
        <ul>
          <div>
          <ChatListItem
            profileUrl="https://upload.wikimedia.org/wikipedia/commons/7/7c/Profile_avatar_placeholder_large.png"
            name="Give me a chance (You)"
            message="https://play.google.com/store/apps/..."
            time="Yesterday"
          />
        </div>
        </ul>
      </div>

      <div className="chat-main">
        <h3>Chat Rooms</h3>
        <div className="chat-messages">
          {messages.map((msg, i) => (
            <div key={i} className="message">
              <strong>{msg.sender}:</strong> {msg.message}
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Tulis pesan..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={sendMessage}>Kirim</button>
        </div>
      </div>
    </div>
  );
}

export default ChatPage;
