import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Register.css';

function Register({ onRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = async () => {
    if (!username || !password) return alert('Isi semua kolom!');
    try {
      const res = await axios.post('http://localhost:5000/api/register', {
        username,
        password
      });
      const { token } = res.data;
      onRegister(token);
    } catch (err) {
      alert('Gagal register: ' + err.response?.data?.error || err.message);
    }
  };

  const handleLogin = (token) => {
    setToken(token); // Menyimpan token setelah login berhasil
    localStorage.setItem('token', token); // Menyimpan token ke localStorage
  };

  return (
    <div className="register-container">
      <div className="register-left">
        <img
          src="https://via.placeholder.com/600x400?text=Vector+Image"
          alt="Vector"
          className="vector-image"
        />
      </div>
      <div className="register-right">
        <h2>Register</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button onClick={handleRegister}>Daftar</button>
        <button onClick={handleRegister}>Login</button>
      </div>
    </div>
  );
}

export default Register;
