const ChatList = () => {
    return (
      <div>
        <h3>Chat Rooms</h3>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {chatData.map(chat => (
            <li key={chat.id} style={{ marginBottom: '10px' }}>
              <ChatListItem
                profileUrl={chat.profileUrl}
                name={chat.name}
                message={chat.message}
                time={chat.time}
              />
            </li>
          ))}
        </ul>
      </div>
    );
  };

export default ChatList;
