import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom'; // Import Navigate
import Login from './pages/Login';
import Register from './pages/Register';
import ChatPage from './pages/ChatPage';
import io from 'socket.io-client'; // Import socket.io-client

function App() {
  const [token, setToken] = useState('');
  const [socket, setSocket] = useState(null); // State to store the socket instance

  const handleLogin = (token) => {
    setToken(token); // Menyimpan token setelah login berhasil
    localStorage.setItem('token', token); // Menyimpan token ke localStorage
  };

  const handleRegister = (token) => {
    setToken(token); // Menyimpan token setelah registrasi berhasil
  };

  // Connect to socket after login
  useEffect(() => {
    if (token) {
      const socketConnection = io('http://localhost:5000', {
        auth: { token }, // Kirim token sebagai bagian dari auth
      });

      socketConnection.on('connect', () => {
        console.log('Connected to socket server');
      });

      socketConnection.on('disconnect', () => {
        console.log('Disconnected from socket server');
      });

      // Save socket connection in state
      setSocket(socketConnection);

      // Clean up socket connection when component is unmounted
      return () => {
        socketConnection.disconnect();
      };
    }
  }, [token]); // Reconnect setiap token berubah

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onRegister={handleRegister} />} />
          <Route
            path="/chatpage"
            element={token ? <ChatPage socket={socket} /> : <Navigate to="/login" />}
          />
          <Route path="/" element={
            token ? (
              <h2>Selamat datang, Anda sudah login!</h2>
            ) : (
              <h2>Silakan Login atau Register</h2>
            )
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
