// ChatPage.js
import React, { useState, useEffect } from 'react';

function ChatPage({ socket }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  // Ensure socket is available before emitting events
  useEffect(() => {
    if (socket) {
      socket.on('message', (msg) => {
        setMessages((prevMessages) => [...prevMessages, msg]);
      });

      return () => {
        socket.off('message'); // Cleanup on unmount
      };
    }
  }, [socket]);

  const sendMessage = () => {
    if (message.trim() && socket) {
      socket.emit('message', { text: message });
      setMessages((prevMessages) => [...prevMessages, { text: message, from: 'Me' }]);
      setMessage('');
    } else {
      console.log('Socket is not ready or message is empty.');
    }
  };

  return (
    <div>
      <h2>Chat Room</h2>
      <div style={{ height: '300px', overflowY: 'scroll' }}>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.from || 'Server'}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Ketik pesan..."
      />
      <button onClick={sendMessage}>Kirim</button>
    </div>
  );
}

export default ChatPage;
